<script> //скрипт изменения цвета активных иконок в меню

$(document).ready(function(){

    var body = $('body').attr("class")

    $(".menu .menu__block").each(function(){

        var  href = $(this).attr("href")

        href= href.replace("/product-category/","").replace("/","")

        if(body.indexOf(href) != -1){

           var img = $(this).find("img")

           var src = img.attr("src")

           src = src.replace(".png","-color.png")

           img.attr("src",src)

           $(this).find("p").css({"color":"white"})

        }



    });


    // скрипт пересчета скидки в корзине
    var input = $("#shipping_method_0_free_shipping2, #shipping_method_0_free_shipping5, #shipping_method_0_free_shipping6"); 



    var price = $(".order-total .woocommerce-Price-amount.amount");

    var price_text = get_price();



    var ckecked = $("#shipping_method_0_free_shipping6").attr('checked', 'checked');



    function get_price(){

        var text = price.text();

            text = text.replace(/\D*/,"").replace(",",".")

            text = parseFloat(text).toFixed(3)

            return text

    }



    function discount(change){

        var text = get_price()

            if(change)

                text = text * 0.9

            return text;

    }



    price.text(discount(true).toFixed(3) + " руб.")



    input.change(function () {

    
    	console.log("yay")
        var id = $(this).attr("id");

        if( id == "shipping_method_0_free_shipping6" ) {

        price.text(discount(true).toFixed(3) + " руб.")

        } else {

        price.text(price_text + " руб.")

        }



    });

})

</script>















